mapper.select:
select user_id, nick_name, name, profile, profile_msg 
from	user_table 
where	user_id = #{user_id}

mapper.update:
update  user_table 
set		nick_name = #{nick_name}, 
 
        profile = #{profile},
 
profile_msg = #{profile_msg} 
where	user_id = #{user_id}

mapper.select:
select 		user_id, nick_name, birth_date, profile, name
from		user_table 
where		user_id = #{user_id}
